mapper.update:
update was_address_list
was_type = #{wasType}, was_address = #{wasAddress}, was_account = #{wasAccount}, was_expire_time = #{wasExpireTime}, was_source = #{wasSource}, was_remark = #{wasRemark}, was_create_time = #{wasCreateTime}
where was_id = #{wasId}

mapper.select:
SELECT * FROM ( SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,was_monitor_time wasMonitorTime,was_create_time wasCreateTime,was_remark,'1' fromT,'已使用' status,was_accept_amount acceptAmount from was_address a UNION ALL SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,'-' wasMonitorTime,'-' wasCreateTime,was_remark,'2' fromT,'未使用' status, '-' acceptAmount from was_address_list b UNION ALL SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,was_monitor_time wasMonitorTime,was_create_time wasCreateTime,was_remark,'3' fromT,'已过期' status,was_accept_amount acceptAmount from was_address_history c )d WHERE 1=1
and d.wasAddress like concat('%',#{bean.wasAddress},'%')
and d.wasType = #{bean.wasType}
and d.wasSource = #{bean.wasSource}
ORDER BY d.wasType,d.wasSource,d.wasExpireTime

select count(1) from (
  SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,was_monitor_time wasMonitorTime,was_create_time wasCreateTime,was_remark,'1' fromT,'已使用' status,was_accept_amount acceptAmount from was_address a
UNION ALL
  SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,'-' wasMonitorTime,'-' wasCreateTime,was_remark,'2' fromT,'未使用' status, '-' acceptAmount from was_address_list b
UNION ALL
  SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,was_monitor_time wasMonitorTime,was_create_time wasCreateTime,was_remark,'3' fromT,'已过期' status,was_accept_amount acceptAmount from was_address_history c
)d WHERE 1=1

    and d.wasAddress like concat('%',#{bean.wasAddress},'%')


    and d.wasType = #{bean.wasType}


    and d.wasSource = #{bean.wasSource}

SELECT * FROM ( SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,was_monitor_time wasMonitorTime,was_create_time wasCreateTime,was_remark,'1' fromT,'已使用' status,was_accept_amount acceptAmount from was_address a UNION ALL SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,'-' wasMonitorTime,'-' wasCreateTime,was_remark,'2' fromT,'未使用' status, '-' acceptAmount from was_address_list b UNION ALL SELECT was_id wasId,was_type wasType,was_source wasSource,was_address wasAddress,was_expire_time wasExpireTime,was_monitor_time wasMonitorTime,was_create_time wasCreateTime,was_remark,'3' fromT,'已过期' status,was_accept_amount acceptAmount from was_address_history c )d WHERE 1=1
and d.wasAddress like concat('%',#{bean.wasAddress},'%')
and d.wasType = #{bean.wasType}
and d.wasSource = #{bean.wasSource}
